import java.util.*;
import java.io.*;
public class CCC19J2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int L = sc.nextInt();

		for (int i = 0; i<L; i++) {
			int N = sc.nextInt();
			String S = sc.next();
			for (int j = 0; j<N; j++) {
				System.out.print(S);

			}
			System.out.println();
		}

	}

}
